import React from 'react' ;
import '../App.css';
import { useAppContext } from './context/appContext';

const ToReadBooks = () => {
    const {toReadBooks, addToToReadBooks, removeFromToReadBooks} = useAppContext();

    console.log("to read books are", toReadBooks);

    const toReadBooksChecker = (id) => {
        const boolean = toReadBooks.some((book) => book.id === id);
        return boolean;
    };
    return (
        <div className='toReadBooks'>
            {toReadBooks.length > 0 ? toReadBooks.map ((book)=>(
                <div key={book.id} className="book">
                    <div>
                        <h3>{book.title}</h3>
                    </div>
                    <div>
                        <img src={book.image_url} alt="#" />
                    </div>
                    <div>
                        <button onClick={()=> removeFromToReadBooks(book.id)}>Usuń z do przeczytania</button>
                    </div>
                </div>
            )):(<h1>Nie masz jeszcze książek do przeczytania!</h1>)}
        </div>
    );

};

export default ToReadBooks;
