import React from 'react' ;
import '../App.css';
import { useAppContext } from './context/appContext';

const ReadBooks = () => {
    const {readBooks} = useAppContext();

    console.log("read books are", readBooks);

    const readBooksChecker = (id) => {
        const boolean = readBooks.some((book) => book.id === id);
        return boolean;
    };
    return (
        <div className='ReadBooks'>
            {readBooks.length > 0 ? readBooks.map ((book)=>(
                <div key={book.id} className="book">
                    <div>
                        <h3>{book.title}</h3>
                    </div>
                    <div>
                        <img src={book.image_url} alt="#" />
                    </div>
                    {/*<div>
                        {ReadBooksChecker(book.id) ? (
                        <button onClick={()=> removeFromToReadBooks(book.id)}>Usuń z ulubionych</button>
                        ) : (
                        <button onClick={()=> addToToReadBooks(book)}>Dodaj do ulubionych</button>
                        )}
                    </div>*/}
                </div>
            )):(<h1>Nie masz jeszcze przeczytanych książek!</h1>)}
        </div>
    );

};

export default ReadBooks;
