import React from 'react' ;
import '../App.css';
import { useAppContext } from './context/appContext';

const ToReadBooks = () => {
    const {toReadBooks, addToToReadBooks, removeFromToReadBooks} = useAppContext();

    /*console.log("favorites are", favorites);

    const favoritesChecker = (id) => {
        const boolean = favorites.some((book) => book.id === id);
        return boolean;
    };*/
    return (
        <div className='toReadBooks'>
            {toReadBooks.length > 0 ? toReadBooks.map ((book)=>(
                <div key={book.id} className="book">
                    <div>
                        <h3>{book.title}</h3>
                    </div>
                    <div>
                        <img src={book.image_url} alt="#" />
                    </div>
                    <div>
                        {toReadBooksChecker(book.id) ? (
                        <button onClick={()=> removeFromToReadBooks(book.id)}>Usuń z ulubionych</button>
                        ) : (
                        <button onClick={()=> addToToReadBooks(book)}>Dodaj do ulubionych</button>
                        )}
                    </div>
                </div>
            )):(<h1>Nie masz jeszcze książek do przeczytania!</h1>)}
        </div>
    );

};

export default ToReadBooks;
